import React, { useContext, useEffect, useState } from "react";
import "./Note.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faArchive,
  faBackward,
  faTrash,
} from "@fortawesome/free-solid-svg-icons";
import { useHistory, useLocation, useParams } from "react-router";
import { putRequest, deleteRequest } from "../../";
import { BASE_URL, UPDATE_NOTE, DELETE_NOTE } from "../../utils/apiEndpoints";
import { NotesContext } from "../../context/contex";
import { noteFormatDate } from "../../utils/helpers";

const Note = () => {
  const history = useHistory();
  const location = useLocation();
  const params = useParams();
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const notesContext = useContext(NotesContext);
  const [updatedAt, setUpdatedAt] = useState("");
  const [isArchive, setIsArchive] = useState(0);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (location.note) {
      setTitle(location.note.title);
      setDesc(location.note.desc);
      setUpdatedAt(location.note.updatedAt);
      setIsArchive(location.note.archive);
    }
  }, [location.note]);

  return (
    <div className="note">
      <div className="note__header">
        <div className="note__header-date">last edited on April 10, 2021</div>
        <div className="noye__header-action-btn">
          <div className="action-btn">
            <FontAwesomeIcon className="icon" icon={faArchive} />
          </div>
        </div>
      </div>
      <div className="note__body">
        <div className="note__body-head">
          <input type="text" placeholder="Title" />
        </div>
        <div className="note__body-content">
          <textarea placeholder="Start writing...."></textarea>
        </div>
      </div>
    </div>
  );
};

export default Note;
