import React, { useEffect, useState, useContext } from "react";
import "./NotesList.scss";
import { useRouteMatch, useHistory, NavLink } from "react-router-dom";
import {
  BASE_URL,
  GET_ALL_NOTES,
  GET_TRASH_NOTES,
} from "../../utils/apiEndpoints";
import { getRequest } from "../../utils/apiRequest";
import { NotesContext } from "../../context/contex";
import { listFormatDate } from "../../utils/hekpers";

const NoteList = (props) => {
  const [error, setError] = useState(null);
  const notesContext = useContext(NotesContext);
  const { title } = props;
  const match = useRouteMatch();
  const history = useHistory();
  useEffect(() => {
    getNotes();
  }, [match.url]);

  return (
    <div className="note-list">
      <div className="note-list__header">
        <div className="note-list__header-title">
          <h1>{title}</h1>
        </div>
        <div className="note-list__header-sub-head">
          <div className="note-count">2 notes</div>
        </div>
      </div>

      {/* body */}
      <div className="note-list__body">
        <div className="note-card">
          <div className="note-card__head">
            <div className="note-card__title">Note One</div>
            <div className="note-card__desc">Some description</div>
          </div>
          <div className="note-card__date">31/04/2021</div>
        </div>
        <div className="note-card">
          <div className="note-card__head">
            <div className="note-card__title">Note One</div>
            <div className="note-card__desc">Some description</div>
          </div>
          <div className="note-card__date">31/04/2021</div>
        </div>
      </div>
    </div>
  );
};

export default NoteList;
